import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <h1>AI Financial Advisor</h1>

      <div class="chat-box">
        <div *ngFor="let msg of messages" [class]="'message ' + msg.type">
          <strong>{{msg.type === 'user' ? 'You' : 'AI'}}:</strong> {{msg.content}}
        </div>
      </div>

      <div class="input-area">
        <input [(ngModel)]="inputMessage" 
               (keyup.enter)="sendMessage()"
               placeholder="Ask about budgeting, investing, etc.">
        <button (click)="sendMessage()" [disabled]="loading">Send</button>
      </div>
    </div>
  `,
  styles: [`
    .container { max-width: 600px; margin: 20px auto; padding: 20px; }
    h1 { text-align: center; color: #333; }
    .chat-box { 
      height: 300px; 
      border: 1px solid #ddd; 
      padding: 10px; 
      overflow-y: auto; 
      margin: 20px 0;
      background: #f9f9f9;
    }
    .message { margin: 10px 0; padding: 8px; border-radius: 5px; }
    .message.user { background: #e3f2fd; }
    .message.bot { background: #f1f8e9; }
    .input-area { display: flex; gap: 10px; }
    input { flex: 1; padding: 10px; border: 1px solid #ddd; border-radius: 4px; }
    button { padding: 10px 20px; background: #007bff; color: white; border: none; border-radius: 4px; cursor: pointer; }
    button:disabled { opacity: 0.5; }
  `]
})
export class AppComponent {
  messages: any[] = [];
  inputMessage = '';
  loading = false;

  constructor(private http: HttpClient) {}

  sendMessage() {
    if (!this.inputMessage.trim()) return;

    this.messages.push({ type: 'user', content: this.inputMessage });
    this.loading = true;

    this.http.post<any>('http://localhost:8001/api/chat', { message: this.inputMessage })
      .subscribe({
        next: (res) => {
          this.messages.push({ type: 'bot', content: res.response });
          this.loading = false;
        },
        error: () => {
          this.messages.push({ type: 'bot', content: 'Error connecting to server' });
          this.loading = false;
        }
      });

    this.inputMessage = '';
  }
}
