import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <h1>AI Financial Advisor</h1>

      <div class="chat-box">
        <div *ngFor="let msg of messages" [class]="'message ' + msg.type">
          <strong>{{msg.type === 'user' ? 'You' : 'AI'}}:</strong> {{msg.content}}
        </div>
      </div>

      <div class="input-area">
        <input [(ngModel)]="inputMessage" 
               (keyup.enter)="sendMessage()"
               placeholder="Ask about budgeting, investing, etc.">
        <button (click)="sendMessage()" [disabled]="loading">Send</button>
      </div>
    </div>
  `,
  styles: [`
    body { background: #f4f6fb; }
    .container {
      max-width: 750px;
      min-height: 700px;
      margin: 40px auto;
      padding: 40px 36px 28px 36px;
      background: #fff;
      border-radius: 18px;
      box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.12);
      border: 1px solid #e3e8f0;
      display: flex;
      flex-direction: column;
      height: 90vh;
      min-height: 600px;
      max-height: 95vh;
      overflow: hidden;
    }
    h1 {
      text-align: center;
      color: #2d3a4a;
      font-size: 2.2rem;
      font-weight: 700;
      letter-spacing: 1px;
      margin-bottom: 18px;
      background: linear-gradient(90deg, #4f8cff 0%, #6ee7b7 100%);
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      background-clip: text;
    }
    .chat-box {
      flex: 1 1 auto;
      min-height: 0;
      border-radius: 12px;
      border: 1.5px solid #e3e8f0;
      padding: 22px 18px;
      overflow-y: auto;
      margin: 28px 0 18px 0;
      background: linear-gradient(135deg, #f7fafc 60%, #e3f0ff 100%);
      box-shadow: 0 2px 8px 0 rgba(31, 38, 135, 0.06);
      transition: box-shadow 0.2s;
      display: flex;
      flex-direction: column;
      max-height: 100%;
    }
    .chat-box::-webkit-scrollbar {
      width: 8px;
      background: #e3e8f0;
      border-radius: 8px;
    }
    .chat-box::-webkit-scrollbar-thumb {
      background: #b6c6e3;
      border-radius: 8px;
    }
    .message {
      margin: 12px 0;
      padding: 12px 18px;
      border-radius: 10px;
      font-size: 1.08rem;
      box-shadow: 0 1px 4px 0 rgba(31, 38, 135, 0.04);
      transition: background 0.2s;
      word-break: break-word;
      line-height: 1.6;
    }
    .message.user {
      background: linear-gradient(90deg, #e3f0ff 60%, #d1f7e7 100%);
      align-self: flex-end;
      border-bottom-right-radius: 2px;
      border-top-right-radius: 16px;
      border-top-left-radius: 16px;
      border-bottom-left-radius: 16px;
      color: #2d3a4a;
      font-weight: 500;
    }
    .message.bot {
      background: linear-gradient(90deg, #f7fafc 60%, #e3e8f0 100%);
      align-self: flex-start;
      border-bottom-left-radius: 2px;
      border-top-right-radius: 16px;
      border-top-left-radius: 16px;
      border-bottom-right-radius: 16px;
      color: #3b4a5a;
    }
    .input-area {
      display: flex;
      gap: 12px;
      margin-top: 8px;
      align-items: center;
      background: #f7fafc;
      border-radius: 8px;
      padding: 10px 12px;
      box-shadow: 0 1px 4px 0 rgba(31, 38, 135, 0.04);
      border: 1px solid #e3e8f0;
    }
    input {
      flex: 1;
      padding: 12px 16px;
      border: none;
      border-radius: 6px;
      font-size: 1.08rem;
      background: #f4f6fb;
      color: #2d3a4a;
      outline: none;
      transition: box-shadow 0.2s;
      box-shadow: 0 1px 2px 0 rgba(31, 38, 135, 0.03);
    }
    input:focus {
      box-shadow: 0 0 0 2px #4f8cff33;
      background: #e3f0ff;
    }
    button {
      padding: 12px 28px;
      background: linear-gradient(90deg, #4f8cff 0%, #6ee7b7 100%);
      color: white;
      border: none;
      border-radius: 6px;
      font-size: 1.08rem;
      font-weight: 600;
      cursor: pointer;
      box-shadow: 0 2px 8px 0 rgba(31, 38, 135, 0.08);
      transition: background 0.2s, box-shadow 0.2s;
    }
    button:disabled {
      opacity: 0.6;
      cursor: not-allowed;
      background: #b6c6e3;
    }
  `]
})
export class AppComponent {
  messages: any[] = [];
  inputMessage = '';
  loading = false;

  constructor(private http: HttpClient) {
    // Load messages from localStorage if available
    const saved = localStorage.getItem('chatbot-messages');
    if (saved) {
      this.messages = JSON.parse(saved);
      setTimeout(() => this.scrollToBottom(), 100);
    }
  }

  sendMessage() {
    if (!this.inputMessage.trim()) return;

    this.messages.push({ type: 'user', content: this.inputMessage });
    this.saveMessages();
    this.loading = true;

    this.http.post<any>('http://localhost:8002/api/chat', { message: this.inputMessage })
      .subscribe({
        next: (res) => {
          this.messages.push({ type: 'bot', content: res.response });
          this.saveMessages();
          this.loading = false;
          setTimeout(() => this.scrollToBottom(), 100);
        },
        error: () => {
          this.messages.push({ type: 'bot', content: 'Error connecting to server' });
          this.saveMessages();
          this.loading = false;
          setTimeout(() => this.scrollToBottom(), 100);
        }
      });

    this.inputMessage = '';
    setTimeout(() => this.scrollToBottom(), 100);
  }

  saveMessages() {
    localStorage.setItem('chatbot-messages', JSON.stringify(this.messages));
  }

  scrollToBottom() {
    const chatBox = document.querySelector('.chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }
}
